mapper.select:
SELECT w1.* FROM ( SELECT w.* FROM ( SELECT row_number () OVER (ORDER BY a.TestingTime DESC) n, CAST ( b.EquipmentNum AS VARCHAR (50) ) AS deviceSerialId, c.DeviceVersionName AS deviceVersion, a.ID AS id, SUBSTRING (a.TestingTime, 1, 19) AS takeTime, a.HeartRate AS heartRate, a.State AS state, a.TimeLength AS timeLength, a.Remark AS feedback, COUNT(a.TestingTime) over() as total FROM ElectrocardioHistory a, Equipment b, DeviceVersion c WHERE a.EquipmentID = b.Id AND b.EquipmentVersion = c.Version
AND SUBSTRING(a.TestingTime,1,19) >= SUBSTRING(#{startDate},1,19)
AND SUBSTRING(a.TestingTime,1,19) <= SUBSTRING(#{endDate},1,19)
AND a.State = #{heartType}
AND a.PatientID = #{patientId}
AND a.PatientID = ''
) w WHERE
n <= #{pageMax}
1 = 1
) w1 WHERE
n >= #{pageMin}
1 = 1
